sys_script_include.script:
function validateEmailAddress(emailStr){
	//JavaScritを使用してemailStrがstringであることを保証する
	emailStr = emailStr + "";
	//emailStrを正規表現で指定されている許可された構文と比較
	//emailStrで許可された構文はtrue、それ以外はfalseを返す
	if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	}
	else{
		return false;
	}
}
